mapper.select:
select *
from(
	select rownum rnum, id, title, cpn, writeDate, hit
	from (
		select id, title, cpn, writeDate, hit
		from furniturelist
		where id > 0
		
		order by id desc
	)
)
where rnum between #{startRow} and #{endRow}

select *
from(
	select rownum rnum, id, title, picture, cpn, writeDate, hit
	from (
		select id, title, picture, cpn, writeDate, hit
		from furniturelist
		where id > 0
		
		order by id desc
	)
)
where rnum between #{startRow} and #{endRow}

select id, title, writeDate, hit,makecp,content,model_id,price
from furniturelist
where id = #{id}

mapper.update:
update furniturelist 
set title = #{title}, cpn = #{cpn}
where id = #{id}
